import './App.css';
import axios from 'axios';
import BookForm from './Book/BookForm';
import BookList from './Book/BookList';
import BookSingle from './Book/BookSingle';
import React from 'react';

class App extends React.Component{
    constructor (props){
        super(props);
        this.state = {
            books: [],
            currentBook: {},
        }
        this.updateCurrentBook = this.updateCurrentBook.bind(this);
        this.viewCurrentBook = this.viewCurrentBook.bind(this);
    }

    componentDidMount(){
        const url = 'http://localhost:4000/book';
        axios.get(url)
            .then((Response) => {
                this.setState({
                    books:Response.data
                })
            })
            .catch((error) => {
                console.log(error);
            })
    }

    updateCurrentBook (item){
        this.setState({
            currentBook: item,
        });
    }

    viewCurrentBook (item){
        this.setState({
            currentBook: item,
        });
    }

    render (){
        return (
            <div className="App">
                <div className="container-fluid">
                    <div className="row">
                        <nav>
                            <div className="nav-wrapper black">
                                <a href="#!" className="brand-logo"><i className="material-icons"></i>Damac Book Store</a>
                            </div>
                        </nav>
                    </div>
                    <div className="row">
                        <div className="col s6"><BookList books={this.state.books}
                            updateCurrentBook={this.updateCurrentBook}
                            viewCurrentBook ={this.viewCurrentBook}/>
                        </div>
                        <div className="col s12"><BookForm/></div>
                    </div>
                    <div className="row">
                        <div className="col s6"><BookSingle books={this.state.currentBook}/></div>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
