import React from 'react';
import axios from 'axios'

class BookForm extends React.Component {

    constructor(props){
        super(props);
        this.refName = React.createRef();
        this.refAuthor = React.createRef();
        this.refAge = React.createRef();
        this.refPrice = React.createRef();
    }

    submitAlpha(event){
        event.preventDefault();
        axios.post('http://localhost:4000/alphas', {
            firstName: this.refFirstName.current.value,
            lastName: this.refLastName.current.value,
            age: this.refAge.current.value,
            ownerEmail: this.refOwnerEmail.current.value
        })
        .then((response) => {
            console.log(response);
        })
        .catch((error) =>{
            console.log(error);
        });
    }

    render() {
        return (
            <div className="row">
                <h3 className="center">Add New Alpha</h3>
                <form className="col s6" onSubmit={this.submitAlpha.bind(this)}>
                    <div className="row">
                        <div className="input-field col s6">
                            <input id="firstName" ref={this.refFirstName} type="text"/>
                                <label htmlFor="firstName">First Name</label>
                        </div>
                        <div className="input-field col s6">
                            <input id="lastName" ref={this.refLastName} type="text"/>
                                <label htmlFor="lastName">First Name</label>
                        </div>
                    </div>
                    <div className="row">
                        <div className="input-field col s6">
                            <input id="age" ref={this.refAge} type="text"/>
                                <label htmlFor="age">Age</label>
                        </div>
                        <div className="input-field col s6">
                            <input id="ownerEmail" ref={this.refOwnerEmail} type="text"/>
                                <label htmlFor="ownerEmail">Email</label>
                        </div>
                    </div>
                    <button className="button waves-effect waves-light" type="submit" name="action">Add Alpha</button>
                </form>
            </div>
        );
    }
}

export default BookForm;
